#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir/node --max-old-space-size=10240$exe") {
  & "$basedir/node --max-old-space-size=10240$exe"  "$basedir/../semver/bin/semver.js" $args
  $ret=$LASTEXITCODE
} else {
  & "node --max-old-space-size=10240$exe"  "$basedir/../semver/bin/semver.js" $args
  $ret=$LASTEXITCODE
}
exit $ret

